"use client";

import { useEffect, useState } from 'react';
import { useRouter } from 'next/navigation';
import { getProjects, createProject, deleteProject } from '../../services/projects';
import { getTasks } from '../../services/tasks';
import Link from 'next/link';
import Navbar from '../../components/Navbar';
import StatCard from '../../components/StatCard';

export default function Dashboard() {
    const router = useRouter();
    const [projects, setProjects] = useState<any[]>([]);
    const [loading, setLoading] = useState(true);
    const [stats, setStats] = useState({ totalProjects: 0, totalTasks: 0, completedTasks: 0 });

    // Modal State for "New Project"
    const [showModal, setShowModal] = useState(false);
    const [newProject, setNewProject] = useState({ name: '', description: '' });

    useEffect(() => {
        fetchData();
    }, []);

    const fetchData = async () => {
        try {
            const [projectsData, tasksData] = await Promise.all([
                getProjects(),
                getTasks()
            ]);

            setProjects(projectsData);

            const completed = tasksData.filter((t: any) => t.status === 'DONE').length;
            setStats({
                totalProjects: projectsData.length,
                totalTasks: tasksData.length,
                completedTasks: completed
            });

        } catch (error) {
            console.error("Failed to load data", error);
            router.push('/login');
        } finally {
            setLoading(false);
        }
    };

    const handleCreate = async (e: React.FormEvent) => {
        e.preventDefault();
        try {
            await createProject(newProject);
            setShowModal(false);
            setNewProject({ name: '', description: '' });
            fetchData();
        } catch (error) {
            alert("Failed to create project");
        }
    };

    const handleDelete = async (id: number) => {
        if (confirm("Are you sure? This will delete all tasks in this project.")) {
            await deleteProject(id);
            fetchData();
        }
    };

    if (loading) return (
        <div className="flex items-center justify-center min-h-screen text-white">
            <div className="animate-pulse">Loading Workspace...</div>
        </div>
    );

    return (
        <div className="min-h-screen pb-20">
            <Navbar />

            {/* Background Gradients */}
            <div className="fixed inset-0 z-[-1] overflow-hidden pointer-events-none">
                <div className="absolute top-0 left-1/4 w-[500px] h-[500px] bg-indigo-600/10 rounded-full blur-[128px]" />
                <div className="absolute bottom-0 right-1/4 w-[500px] h-[500px] bg-purple-600/10 rounded-full blur-[128px]" />
            </div>

            <main className="max-w-6xl mx-auto p-8 pt-28">

                {/* PDF 3.4: Summary Statistics */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 mb-12">
                    <StatCard label="Total Projects" value={stats.totalProjects} />
                    <StatCard label="Total Tasks" value={stats.totalTasks} />
                    <StatCard label="Completed Tasks" value={stats.completedTasks} />
                </div>

                {/* Projects Header */}
                <div className="flex justify-between items-end mb-8 border-b border-neutral-800 pb-4">
                    <div>
                        <h2 className="text-3xl font-bold bg-gradient-to-r from-white to-neutral-400 bg-clip-text text-transparent">Your Projects</h2>
                        <p className="text-neutral-500 mt-1">Manage and track your ongoing work</p>
                    </div>
                    <button
                        onClick={() => setShowModal(true)}
                        className="bg-indigo-600 hover:bg-indigo-500 text-white px-5 py-2.5 rounded-xl text-sm font-medium transition-all shadow-lg shadow-indigo-500/20 active:scale-95"
                    >
                        + New Project
                    </button>
                </div>

                {/* Projects Grid */}
                <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
                    {projects.length === 0 ? (
                        <div className="col-span-3 text-center py-20 border border-dashed border-neutral-800 rounded-2xl">
                            <p className="text-neutral-500 mb-4">No projects yet</p>
                            <button onClick={() => setShowModal(true)} className="text-indigo-400 hover:text-indigo-300 text-sm font-medium">Create your first project &rarr;</button>
                        </div>
                    ) : (
                        projects.map((project) => (
                            <div key={project.id} className="card group relative hover:-translate-y-1 transition-transform duration-300">
                                <Link href={`/projects/${project.id}`} className="absolute inset-0 z-10" />

                                <div className="flex justify-between items-start mb-4">
                                    <h3 className="text-xl font-bold text-white group-hover:text-indigo-400 transition-colors">{project.name}</h3>
                                    <span className="text-xs text-neutral-500 bg-neutral-800/50 px-2 py-1 rounded-md border border-neutral-800">
                                        {new Date(project.created_at).toLocaleDateString()}
                                    </span>
                                </div>

                                <p className="text-neutral-400 text-sm mb-6 h-10 line-clamp-2 leading-relaxed">{project.description}</p>

                                <div className="flex justify-end pt-4 border-t border-neutral-800 relative z-20">
                                    <button
                                        onClick={(e) => {
                                            e.stopPropagation();
                                            handleDelete(project.id);
                                        }}
                                        className="text-neutral-500 hover:text-red-400 text-xs font-medium transition-colors px-2 py-1 hover:bg-red-500/10 rounded"
                                    >
                                        Delete Project
                                    </button>
                                </div>
                            </div>
                        ))
                    )}
                </div>
            </main>

            {/* Modal */}
            {showModal && (
                <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-[100]">
                    <div className="bg-[#171717] border border-[#262626] p-8 rounded-2xl w-full max-w-md shadow-2xl animate-in fade-in zoom-in duration-200">
                        <h2 className="text-2xl font-bold text-white mb-1">New Project</h2>
                        <p className="text-neutral-400 text-sm mb-6">Create a space for your tasks</p>

                        <form onSubmit={handleCreate} className="space-y-4">
                            <div>
                                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Project Name</label>
                                <input
                                    className="input-field"
                                    placeholder="e.g. Website Redesign"
                                    value={newProject.name}
                                    onChange={(e) => setNewProject({ ...newProject, name: e.target.value })}
                                    required
                                    autoFocus
                                />
                            </div>
                            <div>
                                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Description</label>
                                <textarea
                                    className="input-field min-h-[100px] resize-none"
                                    placeholder="What is this project about?"
                                    rows={3}
                                    value={newProject.description}
                                    onChange={(e) => setNewProject({ ...newProject, description: e.target.value })}
                                    required
                                />
                            </div>

                            <div className="flex gap-3 mt-6">
                                <button
                                    type="button"
                                    onClick={() => setShowModal(false)}
                                    className="flex-1 py-3 bg-neutral-800 text-neutral-300 rounded-xl hover:bg-neutral-700 transition-colors font-medium border border-neutral-700"
                                >
                                    Cancel
                                </button>
                                <button
                                    type="submit"
                                    className="flex-1 py-3 bg-indigo-600 text-white rounded-xl hover:bg-indigo-500 transition-colors font-medium shadow-lg shadow-indigo-500/20"
                                >
                                    Create Project
                                </button>
                            </div>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

"use client";

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import { loginUser } from '../../services/auth';
import AuthNavbar from '../../components/AuthNavbar';

export default function LoginPage() {
    const [formData, setFormData] = useState({ username: '', password: '' });
    const [error, setError] = useState('');
    const [loading, setLoading] = useState(false);
    const router = useRouter();

    const handleSubmit = async (e: React.FormEvent) => {
        e.preventDefault();
        setLoading(true);
        setError('');

        try {
            await loginUser(formData);
            router.push('/dashboard');
        } catch (err: any) {
            setError('Invalid credentials. Please try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <>
            <AuthNavbar />

            {/* Background Gradients */}
            <div className="fixed inset-0 z-[-1] overflow-hidden pointer-events-none">
                <div className="absolute top-0 left-1/4 w-96 h-96 bg-indigo-600/20 rounded-full blur-[128px]" />
                <div className="absolute bottom-0 right-1/4 w-96 h-96 bg-purple-600/10 rounded-full blur-[128px]" />
            </div>

            <div className="min-h-screen flex items-center justify-center p-4 pt-20">
                <div className="w-full max-w-md card">
                    <div className="text-center mb-8">
                        <h1 className="heading-xl bg-gradient-to-br from-white to-gray-400">
                            Welcome Back
                        </h1>
                        <p className="mt-2 text-sm text-neutral-400">
                            Enter your credentials to access your workspace
                        </p>
                    </div>

                    <form className="space-y-6" onSubmit={handleSubmit}>
                        {error && (
                            <div className="bg-red-500/10 border border-red-500/20 text-red-400 text-sm p-3 rounded-lg text-center">
                                {error}
                            </div>
                        )}

                        <div className="space-y-4">
                            <div>
                                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Username</label>
                                <input
                                    type="text"
                                    required
                                    className="input-field"
                                    placeholder="Enter your username"
                                    value={formData.username}
                                    onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                                />
                            </div>
                            <div>
                                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Password</label>
                                <input
                                    type="password"
                                    required
                                    className="input-field"
                                    placeholder="Enter your password"
                                    value={formData.password}
                                    onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                                />
                            </div>
                        </div>

                        <button
                            type="submit"
                            disabled={loading}
                            className="btn-primary"
                        >
                            {loading ? 'Authenticating...' : 'Sign In'}
                        </button>

                        <p className="text-center text-sm text-neutral-500">
                            Don't have an account?{' '}
                            <Link href="/register" className="font-medium text-indigo-400 hover:text-indigo-300 transition-colors">
                                Create Account
                            </Link>
                        </p>
                    </form>
                </div>
            </div>
        </>
    );
}

"use client";

import { useEffect, useState } from 'react';
import { useParams, useRouter } from 'next/navigation';
import Link from 'next/link';
import { getProjectById } from '../../../services/projects';
import { createTask, updateTaskStatus, deleteTask } from '../../../services/tasks';
import Navbar from '@/components/Navbar';

export default function ProjectDetails() {
    const { id } = useParams();
    const router = useRouter();

    const [project, setProject] = useState<any>(null);
    const [loading, setLoading] = useState(true);

    const [showTaskModal, setShowTaskModal] = useState(false);
    const [newTask, setNewTask] = useState({ title: '', description: '', status: 'TODO' });
    const [isSubmitting, setIsSubmitting] = useState(false);

    useEffect(() => {
        if (id) fetchProjectData();
    }, [id]);

    const fetchProjectData = async () => {
        try {
            const data = await getProjectById(id as string);
            setProject(data);
        } catch (error) {
            console.error("Failed to fetch project", error);
        } finally {
            setLoading(false);
        }
    };

    const handleAddTask = async (e: React.FormEvent) => {
        e.preventDefault();
        setIsSubmitting(true);
        try {
            await createTask({ ...newTask, project: Number(id) });
            setNewTask({ title: '', description: '', status: 'TODO' });
            setShowTaskModal(false);
            fetchProjectData();
        } catch (error) {
            alert("Failed to add task");
        } finally {
            setIsSubmitting(false);
        }
    };

    const handleStatusChange = async (taskId: number, newStatus: string) => {
        // Optimistic update could go here, but strict fetch for now
        try {
            await updateTaskStatus(taskId, newStatus);
            fetchProjectData();
        } catch (error) {
            console.error("Failed to update status", error);
        }
    };

    const handleDeleteTask = async (taskId: number) => {
        if (confirm("Delete this task?")) {
            await deleteTask(taskId);
            fetchProjectData();
        }
    };

    if (loading) return (
        <div className="flex items-center justify-center min-h-screen text-white">
            <div className="animate-pulse">Loading Project...</div>
        </div>
    );

    if (!project) return (
        <div className="flex items-center justify-center min-h-screen text-white">
            <div className="text-center">
                <h1 className="text-2xl font-bold">Project Not Found</h1>
                <Link href="/dashboard" className="text-indigo-400 hover:underline mt-4 block">Return to Dashboard</Link>
            </div>
        </div>
    );

    // Group tasks by status
    const tasksByStatus = {
        TODO: project.tasks?.filter((t: any) => t.status === 'TODO') || [],
        IN_PROGRESS: project.tasks?.filter((t: any) => t.status === 'IN_PROGRESS') || [],
        DONE: project.tasks?.filter((t: any) => t.status === 'DONE') || []
    };

    return (
        <div className="min-h-screen pb-20">
            <Navbar />

            {/* Background Gradients */}
            <div className="fixed inset-0 z-[-1] overflow-hidden pointer-events-none">
                <div className="absolute top-0 right-1/4 w-[500px] h-[500px] bg-indigo-600/10 rounded-full blur-[128px]" />
                <div className="absolute bottom-0 left-1/4 w-[500px] h-[500px] bg-purple-600/10 rounded-full blur-[128px]" />
            </div>

            <div className="max-w-[1400px] mx-auto p-6 pt-24">
                {/* Header */}
                <div className="flex flex-col md:flex-row md:items-center justify-between gap-4 mb-8">
                    <div>
                        <Link href="/dashboard" className="text-sm text-neutral-500 hover:text-white mb-2 inline-flex items-center transition-colors">
                            ΓåÉ Back to Dashboard
                        </Link>
                        <h1 className="text-3xl font-bold text-white mt-1">{project.name}</h1>
                        <p className="text-neutral-400 mt-2 max-w-2xl">{project.description}</p>
                    </div>
                    <button
                        onClick={() => setShowTaskModal(true)}
                        className="bg-indigo-600 hover:bg-indigo-500 text-white px-5 py-2.5 rounded-xl text-sm font-medium transition-all shadow-lg shadow-indigo-500/20 active:scale-95 whitespace-nowrap"
                    >
                        + Add Task
                    </button>
                </div>

                {/* Kanban Board */}
                <div className="grid grid-cols-1 md:grid-cols-3 gap-6 h-full items-start">
                    {/* Column: TODO */}
                    <KanbanColumn
                        title="To Do"
                        color="bg-yellow-500/10 text-yellow-500 border-yellow-500/20"
                        tasks={tasksByStatus.TODO}
                        onStatusChange={handleStatusChange}
                        onDelete={handleDeleteTask}
                    />

                    {/* Column: IN PROGRESS */}
                    <KanbanColumn
                        title="In Progress"
                        color="bg-blue-500/10 text-blue-500 border-blue-500/20"
                        tasks={tasksByStatus.IN_PROGRESS}
                        onStatusChange={handleStatusChange}
                        onDelete={handleDeleteTask}
                    />

                    {/* Column: DONE */}
                    <KanbanColumn
                        title="Done"
                        color="bg-green-500/10 text-green-500 border-green-500/20"
                        tasks={tasksByStatus.DONE}
                        onStatusChange={handleStatusChange}
                        onDelete={handleDeleteTask}
                    />
                </div>
            </div>

            {/* Task Modal */}
            {showTaskModal && (
                <div className="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center p-4 z-[100]">
                    <div className="bg-[#171717] border border-[#262626] p-8 rounded-2xl w-full max-w-md shadow-2xl animate-in fade-in zoom-in duration-200">
                        <div className="flex justify-between items-center mb-6">
                            <h2 className="text-2xl font-bold text-white">Add New Task</h2>
                            <button onClick={() => setShowTaskModal(false)} className="text-neutral-500 hover:text-white">Γ£ò</button>
                        </div>

                        <form onSubmit={handleAddTask} className="space-y-4">
                            <div>
                                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Title</label>
                                <input
                                    className="input-field"
                                    placeholder="Task title"
                                    value={newTask.title}
                                    onChange={(e) => setNewTask({ ...newTask, title: e.target.value })}
                                    required
                                    autoFocus
                                />
                            </div>
                            <div>
                                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Description</label>
                                <textarea
                                    className="input-field min-h-[100px] resize-none"
                                    placeholder="Task details..."
                                    rows={3}
                                    value={newTask.description}
                                    onChange={(e) => setNewTask({ ...newTask, description: e.target.value })}
                                    required
                                />
                            </div>
                            <div>
                                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Status</label>
                                <select
                                    className="input-field appearance-none"
                                    value={newTask.status}
                                    onChange={(e) => setNewTask({ ...newTask, status: e.target.value })}
                                >
                                    <option value="TODO">To Do</option>
                                    <option value="IN_PROGRESS">In Progress</option>
                                    <option value="DONE">Done</option>
                                </select>
                            </div>

                            <button
                                type="submit"
                                disabled={isSubmitting}
                                className="w-full mt-4 py-3 bg-indigo-600 text-white rounded-xl hover:bg-indigo-500 transition-colors font-medium shadow-lg shadow-indigo-500/20"
                            >
                                {isSubmitting ? 'Adding...' : 'Create Task'}
                            </button>
                        </form>
                    </div>
                </div>
            )}
        </div>
    );
}

// Helper Component for Kanban Columns
function KanbanColumn({ title, color, tasks, onStatusChange, onDelete }: any) {
    return (
        <div className="flex flex-col gap-4">
            <div className={`p-4 rounded-3xl border ${color} flex justify-between items-center backdrop-blur-sm shadow-sm`}>
                <h3 className="font-bold text-sm tracking-wide uppercase">{title}</h3>
                <span className="text-xs font-bold bg-white/20 px-3 py-1 rounded-full text-white backdrop-blur-md">{tasks.length}</span>
            </div>

            <div className="flex flex-col gap-3">
                {tasks.length === 0 && (
                    <div className="text-center py-8 border border-dashed border-neutral-800 rounded-xl text-neutral-600 text-sm">
                        No tasks
                    </div>
                )}
                {tasks.map((task: any) => (
                    <div key={task.id} className="card p-4 hover:border-neutral-700 group relative">
                        <div className="flex justify-between items-start mb-2">
                            <h4 className={`font-semibold text-neutral-200 ${task.status === 'DONE' ? 'line-through text-neutral-500' : ''}`}>
                                {task.title}
                            </h4>
                            <div className="relative">
                                {/* Simple Dropdown for moving tasks */}
                                <select
                                    className="opacity-0 absolute inset-0 w-full h-full cursor-pointer"
                                    value={task.status}
                                    onChange={(e) => onStatusChange(task.id, e.target.value)}
                                >
                                    <option value="TODO">To Do</option>
                                    <option value="IN_PROGRESS">In Progress</option>
                                    <option value="DONE">Done</option>
                                </select>
                                <button className="text-xs text-neutral-500 hover:text-white p-1 rounded hover:bg-neutral-800">
                                    Move Γû╛
                                </button>
                            </div>
                        </div>
                        <p className="text-sm text-neutral-400 mb-3 line-clamp-3">{task.description}</p>

                        <div className="flex justify-between items-center pt-3 border-t border-neutral-800/50">
                            <span className="text-[10px] text-neutral-600 uppercase tracking-wider font-medium">
                                Task #{task.id}
                            </span>
                            <button
                                onClick={() => onDelete(task.id)}
                                className="text-xs text-red-400/0 group-hover:text-red-400 transition-all hover:underline"
                            >
                                Delete
                            </button>
                        </div>
                    </div>
                ))}
            </div>
        </div>
    );
}

"use client";

import { useState } from 'react';
import { useRouter } from 'next/navigation';
import Link from 'next/link';
import { registerUser } from '@/services/auth';
import AuthNavbar from '@/components/AuthNavbar';

export default function RegisterPage() {
  const [formData, setFormData] = useState({ username: '', email: '', password: '' });
  const [error, setError] = useState('');
  const [loading, setLoading] = useState(false);
  const router = useRouter();

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    setLoading(true);
    setError('');

    try {
      await registerUser(formData);
      alert("Account created! Please log in.");
      router.push('/login');
    } catch (err: any) {
      if (typeof err === 'string') {
        setError(err);
      } else if (err.response?.data) {
        const data = err.response.data;
        const firstKey = Object.keys(data)[0];
        if (firstKey && Array.isArray(data[firstKey])) {
          setError(`${firstKey}: ${data[firstKey][0]}`);
        } else {
          setError(JSON.stringify(data));
        }
      } else {
        setError("Registration failed. Please try again.");
      }
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <AuthNavbar />

      {/* Background Gradients */}
      <div className="fixed inset-0 z-[-1] overflow-hidden pointer-events-none">
        <div className="absolute top-0 right-1/4 w-96 h-96 bg-indigo-600/20 rounded-full blur-[128px]" />
        <div className="absolute bottom-0 left-1/4 w-96 h-96 bg-purple-600/10 rounded-full blur-[128px]" />
      </div>

      <div className="min-h-screen flex items-center justify-center p-4 pt-20">
        <div className="w-full max-w-md card">
          <div className="text-center mb-8">
            <h1 className="heading-xl bg-gradient-to-br from-white to-gray-400">
              Create Account
            </h1>
            <p className="mt-2 text-sm text-neutral-400">
              Start managing your projects like a pro
            </p>
          </div>

          <form className="space-y-6" onSubmit={handleSubmit}>
            {error && (
              <div className="bg-red-500/10 border border-red-500/20 text-red-400 text-sm p-3 rounded-lg text-center">
                {error}
              </div>
            )}

            <div className="space-y-4">
              <div>
                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Username</label>
                <input
                  type="text"
                  required
                  className="input-field"
                  placeholder="Choose a username"
                  value={formData.username}
                  onChange={(e) => setFormData({ ...formData, username: e.target.value })}
                />
              </div>
              <div>
                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Email</label>
                <input
                  type="email"
                  required
                  className="input-field"
                  placeholder="Enter your email"
                  value={formData.email}
                  onChange={(e) => setFormData({ ...formData, email: e.target.value })}
                />
              </div>
              <div>
                <label className="block text-xs font-medium text-neutral-500 mb-1 ml-1 uppercase tracking-wider">Password</label>
                <input
                  type="password"
                  required
                  className="input-field"
                  placeholder="Create a password"
                  value={formData.password}
                  onChange={(e) => setFormData({ ...formData, password: e.target.value })}
                />
              </div>
            </div>

            <button
              type="submit"
              disabled={loading}
              className="btn-primary"
            >
              {loading ? 'Creating account...' : 'Sign Up'}
            </button>

            <p className="text-center text-sm text-neutral-500">
              Already have an account?{' '}
              <Link href="/login" className="font-medium text-indigo-400 hover:text-indigo-300 transition-colors">
                Sign in
              </Link>
            </p>
          </form>
        </div>
      </div>
    </>
  );
}

messages.message:
Error: Calling setState synchronously within an effect can trigger cascading renders

Effects are intended to synchronize state between React and external systems such as manually updating the DOM, state management libraries, or other platform APIs. In general, the body of an effect should do one or both of the following:
* Update external systems with the latest state from React.
* Subscribe for updates from some external system, calling setState in a callback function when external state changes.

Calling setState synchronously within an effect body causes cascading renders that can hurt performance, and is not recommended. (https://react.dev/learn/you-might-not-need-an-effect).

